// import Main from './components/Main'
import { useEffect, useState } from 'react'
import Movies from './Movies'
import Search from './Search'
import { request } from '../utils/request'

function Main() {
  // Liste med informasjon om Star Wars filmene hente fra OmdbApi
  // Bruker fetch med .then()
  // http://www.omdbapi.com/?s=Star Wars&apikey=5cc65390
  const [movies, setMovies] = useState()
  const [search, setSearch] = useState()
  // Bruker useEffect for å få vise 5 filmer med søkeorder Star Wars
  useEffect(() => {
    fetch('http://www.omdbapi.com/?search=Star%20Wars&apikey=5cc65390')
      .then((response) => response.json())
      .then((data) => setMovies(data.Search.splice(0, 5)))
      .catch((err) => console.log(err))
  }, [movies])

  const handleSearch = (event) => {
    event.preventDefault()
    const jokes = request(`http://www.omdbapi.com/?s=${search}&apikey=5cc65390`)
  }

  const handleInput = (event) => {
    setSearch(event.currentTarget.value)
    console.log(search)
  }

  return (
    <main>
      <Search search={search} setSearch={setSearch} handleInput={handleInput} />
      <Movies movies={movies} />
    </main>
  )
}

export default Main
