import client from './client'

const movieFields = `
  title,
  "slug": slug.current,
  "movie": movie->fullname
`

const actorFields = `
  fullname,
  "slug": slug.current,
`

// Funksjon for 책 hente alle filmer
export const getMovies = async () => {
  const data = await client.fetch(`*[_type == "movie"]{${movieFields}}`)
  return data
}

export const getMovie = async (slug) => {
  const data = await client.fetch(
    `*[_type == "movie" && slug.current == $slug]{${movieFields}}`,
    { slug }
  )
  return data
}

// Funksjon for 책 hente alle skuespillere
export const getActors = async () => {
  const data = await client.fetch(`*[_type == "actor"]{${actorFields}}`)
  return data
}

export const getActor = async (slug) => {
  const data = await client.fetch(
    `*[_type == "actor" && slug.current == $slug]{${actorFields}}`,
    { slug }
  )
  return data
}
