import { useState } from 'react'

export default function Pokemon({ url, name }) {
  const [show, setShow] = useState(false)
  const showExperience = () => {
    setShow(!show)
    console.log(show)
  }
  console.log(url)

  return (
    <article>
      <h2>{name}</h2>
      {show ? <p>{url.base_experience}</p> : null}
      <button type="button" onClick={showExperience}>
        See experience
      </button>
    </article>
  )
}
