import { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import Answer from '../components/Answer'
import Question from '../components/Question'
import { getQuizBySlug } from '../lib/services/quiz'

function Quiz() {
  const [current, setCurrent] = useState(0)
  const [content, setContent] = useState(null)
  const [answers, setAnswers] = useState([])
  const currentQuestion = content?.questions[current]
  const { slug } = useParams()

  useEffect(() => {
    const getQuiz = async () => {
      const data = await getQuizBySlug(slug)
      setContent(data)
    }
    getQuiz()
  }, [slug])

  const updateAnswer = (title) => {
    setAnswer(title)
  }

  const progress = () => {
    setCurrent((prev) => prev + 1)
  }

  return (
    <div>
      <h1 className="mb3 text-2xl font-bold text-white">Quiz</h1>
      <section key={currentQuestion?._key}>
        <Question title={currentQuestion?.title} />
        {currentQuestion?.answers?.map((answer) => (
          <Answer
            key={answer._key}
            title={answer.title}
            correct={answer.correct}
            updateAnswer={updateAnswer}
          />
        ))}
        <button
          type="button"
          className="rounded bg-orange-400 px-4 py-4 text-white"
          onClick={progress}
        >
          Neste
        </button>
      </section>
    </div>
  )
}

export default Quiz
