import { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import Answer from '../components/Answer'
import Question from '../components/Question'
import { getQuizBySlug } from '../lib/services/quiz'

function Quiz() {
  const [current, setCurrent] = useState(null)
  const [content, setContent] = useState(null)
  const [answer, setAnswer] = useState()
  const currentQuestion = content?.question[current]
  const { slug } = useParams()

  useEffect(() => {
    const getQuiz = async () => {
      const data = await getQuizBySlug(slug)
      setContent(data)
    }
    getQuiz()
  }, [slug])

  const updateAnswer = (title) => {
    setAnswer(title)
  }

  return (
    <div>
      <h1 className="mb3 text-2xl font-bold text-white">Quiz</h1>
      <section key={currentQuestion?._key}>
        <Question title={currentQuestion?.title} />
        {currentQuestion?.answers?.map((answer) => (
          <Answer
            key={answer._key}
            title={answer.title}
            correct={answer.correct}
            updateAnswer={updateAnswer}
          />
        ))}
      </section>
    </div>
  )
}

export default Quiz
