import { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import Answer from '../components/Answer'
import Question from '../components/Question'
import { getQuizBySlug } from '../lib/services/quiz'

function Quiz() {
  const [content, setContent] = useState(null)
  const { slug } = useParams()
  const [answer, setAnswer] = useState()

  useEffect(() => {
    const getQuiz = async () => {
      const data = await getQuizBySlug(slug)
      setContent(data)
    }
    getQuiz()
  }, [slug])

  const updateAnswer = (title) => {
    const { value } = event.target
    setAnswer(value)
  }

  return (
    <div>
      <h1 className="mb3 text-2xl font-bold text-white">Quiz</h1>
      {content?.questions?.map((question) => (
        <section key={question._key}>
          <Question title={question.title} />
          {/* {JSON.stringify(question.answers)} */}
          {question?.answers?.map((answer) => (
            <section key={answer._key}>
              {/* {JSON.stringify(answer.title)} */}
              <Answer
                key={answer._key}
                title={answer.title}
                correct={answer.correct}
                updateAnswer={updateAnswer}
              />
            </section>
          ))}
        </section>
      ))}
    </div>
  )
}

export default Quiz
