function Form() {
  return
  ;<form method="POST" onSubmit={addComment}>
    <h2>Legg igjen en kommentar</h2>
    <label htmlFor="comment">
      Kommentar
      <textarea
        onChange={handleCommentChange}
        id="comment"
        name="comment"
        cols={15}
        rows={5}
        value={comment}
      />
    </label>
    <button type="submit">{loading ? 'Sender ...' : 'Send'}</button>
    {status ? <p>Kommentar sendt</p> : null}
  </form>
}

export default Form
